<script>document.addEventListener('DOMContentLoaded', function() {
    // ################### منطق قائمة التنقل (الهمبرغر) ###################
    const menuToggle = document.getElementById('menuToggle');
    const navLinks = document.getElementById('navLinks');
    if (menuToggle && navLinks) {
        menuToggle.addEventListener('click', () => {
            // تبديل فئة 'active' لإظهار/إخفاء القائمة
            navLinks.classList.toggle('active');
        });
    }
    // ################### منطق إظهار/إخفاء تفاصيل البوكسات ###################
    const boxHeaders = document.querySelectorAll('.box-header');
    // إغلاق جميع التفاصيل الأخرى
    function closeAllDetails(currentDetails) {
        document.querySelectorAll('.box-details').forEach(details => {
            if (details !== currentDetails && !details.classList.contains('hidden')) {
                details.classList.add('hidden');
            }
        });
    }
    // إخفاء جميع التفاصيل عند التحميل الأولي
    document.querySelectorAll('.box-details').forEach(details => {
        details.classList.add('hidden');
    });
    boxHeaders.forEach(header => {
        header.addEventListener('click', function(event) {
            event.stopPropagation();
            const box = this.closest('.action-box');
            if (!box) return;
            const details = box.querySelector('.box-details');
            if (details) {
                // 1. إغلاق جميع الصناديق الأخرى
                closeAllDetails(details);
                // 2. تبديل (إظهار/إخفاء) الصندوق الذي تم الضغط عليه
                details.classList.toggle('hidden');
            }
        });
    });
});
</script>
<script src="https://unpkg.com/aos@2.3.1/dist/aos.js"></script>
<script>
  AOS.init({
    // هذه الإعدادات اختيارية، لكنها مفيدة لضمان التناسق
    offset: 200,      // يبدأ التحريك قبل 200px من رؤية العنصر
    duration: 800,    // مدة التحريك بالمللي ثانية
    easing: 'ease-in-out', // نوع تسارع التحريك
    once: true        // التحريك يحدث مرة واحدة فقط عند النزول لأول مرة
  });
</script>
<script>document.addEventListener('DOMContentLoaded', function() {
    const buttons = document.querySelectorAll('.accordion-button');

    buttons.forEach(button => {
        button.addEventListener('click', function() {
            const contentId = this.getAttribute('data-target');
            const content = document.getElementById(contentId);
            const isExpanded = this.getAttribute('aria-expanded') === 'true';

            // 1. إغلاق جميع المحتويات المفتوحة حالياً
            // هذا يضمن أن يكون سؤال واحد فقط مفتوحاً في كل مرة (Accordion)
            document.querySelectorAll('.accordion-button[aria-expanded="true"]').forEach(btn => {
                if (btn !== this) {
                    btn.setAttribute('aria-expanded', 'false');
                    document.getElementById(btn.getAttribute('data-target')).classList.remove('open');
                }
            });

            // 2. تبديل حالة العنصر الحالي
            if (isExpanded) {
                this.setAttribute('aria-expanded', 'false');
                content.classList.remove('open');
            } else {
                this.setAttribute('aria-expanded', 'true');
                content.classList.add('open');
            }
        });
    });
});</script>
<script>document.addEventListener('DOMContentLoaded', () => {
    // تحديد جميع العناصر الضرورية داخل السكشن
    const dots = document.querySelectorAll('.med-sec-dot');
    const quotes = document.querySelectorAll('.med-sec-single-quote');
    const maxIndex = quotes.length - 1;
    let currentIndex = 0; // لتعقب الشهادة المعروضة حاليًا

    // الدالة الرئيسية للتبديل بين الشهادات
    function showQuote(indexToShow) {
        // 1. إزالة حالة النشاط من النقطة والشهادة الحالية
        document.querySelector('.med-sec-dot.active')?.classList.remove('active');
        document.querySelector('.med-sec-single-quote.active-quote')?.classList.remove('active-quote');

        // 2. تطبيق حالة النشاط على النقطة والشهادة الجديدة
        if (quotes[indexToShow]) {
            quotes[indexToShow].classList.add('active-quote');
            dots[indexToShow].classList.add('active');
            currentIndex = indexToShow; // تحديث المؤشر الحالي
        }
    }

    // --- 1. وظيفة التبديل التلقائي ---

    // الدالة المسؤولة عن الانتقال إلى الشهادة التالية
    function nextQuote() {
        // إذا كان المؤشر الحالي هو الأخير، نعود للبداية (0)، وإلا ننتقل للذي يليه.
        const nextIndex = (currentIndex < maxIndex) ? currentIndex + 1 : 0;
        showQuote(nextIndex);
    }

    // تشغيل التبديل التلقائي كل 5000 مللي ثانية (5 ثوانٍ)
    const intervalTime = 5000;
    let autoSlide = setInterval(nextQuote, intervalTime);

    // --- 2. وظيفة التبديل عند الضغط (اليدوي) ---

    // إضافة مستمع حدث (Event Listener) لكل نقطة
    dots.forEach(dot => {
        dot.addEventListener('click', (event) => {
            // أ. مسح المؤقت التلقائي عند الضغط اليدوي
            clearInterval(autoSlide);

            // ب. عرض الشهادة المطلوبة
            const index = parseInt(event.target.getAttribute('data-index'));
            showQuote(index);

            // ج. إعادة تشغيل المؤقت التلقائي بعد فترة قصيرة
            // (هذا يمنع التبديل الفوري بعد الضغط)
            autoSlide = setInterval(nextQuote, intervalTime);
        });
    });
});</script>
